  transition: all var(--animationTimer) ease;
  height: 0.3rem;
  margin: 0 1px;
  border: 1px solid var(--sd-text-color);
}

#sd-progress .sd-progression:hover {
  background-color: var(--sd-text-color);
  border-color: currentColor;
}

#sd-progress .sd-progression.active {
  background-color: var(--sd-primary-color);
  border-color: var(--sd-primary-color);
}

