addSpeakerScripts../plugins/gamepad-notes/gamepad.js:
let mapping = {};

let throttleTimer;

const throttle = (callback, time) => {
  if (throttleTimer) return;
  throttleTimer = true;
  setTimeout(() => {
    callback();
    throttleTimer = false;
  }, time);
};

const buttonPressed = (b) => {
  if (typeof b === "object") {
    return b.pressed;
  }
  return b === 1.0;
};

const gameLoop = () => {
  const gamepads = navigator.getGamepads();
  if (!gamepads) {
    return;
  }

  const gp = gamepads[0];
  for (let i = 0; i < 16; i += 1)
    if (buttonPressed(gp.buttons[i]) && mapping[i]) {
      window.slidesk.io.send(JSON.stringify({ action: mapping[i] }));
    }

  requestAnimationFrame(() => throttle(gameLoop, 300));
};

const fetchMapping = async () => {
  try {
    const response = await fetch("/mapping.json");
    const json = await response.json();
    mapping = json;
  } catch (erreur) {
    mapping = {
      15: "next",
      14: "previous",
      6: "next",
      7: "previous",
    };
  }
  window.addEventListener("gamepadconnected", () => {
    gameLoop();
  });
};

fetchMapping();

addScripts../plugins/gamepad/gamepad.js:
let mapping = {};

let throttleTimer;

const throttle = (callback, time) => {
  if (throttleTimer) return;
  throttleTimer = true;
  setTimeout(() => {
    callback();
    throttleTimer = false;
  }, time);
};

const buttonPressed = (b) => {
  if (typeof b === "object") {
    return b.pressed;
  }
  return b === 1.0;
};

const gameLoop = () => {
  const gamepads = navigator.getGamepads();
  if (!gamepads) {
    return;
  }

  const gp = gamepads[0];
  for (let i = 0; i < 16; i += 1)
    if (buttonPressed(gp.buttons[i]) && mapping[i]) {
      switch (mapping[i]) {
        case "previous":
          window.slidesk.previous();
          break;
        case "next":
          window.slidesk.next();
          break;
        default:
          break;
      }
    }

  requestAnimationFrame(() =>
    throttle(gameLoop, window.slidesk.animationTimer),
  );
};

const fetchMapping = async () => {
  try {
    const response = await fetch("/mapping.json");
    const json = await response.json();
    mapping = json;
  } catch (erreur) {
    mapping = {
      15: "next",
      14: "previous",
      6: "next",
      7: "previous",
    };
  }
  window.addEventListener("gamepadconnected", () => {
    gameLoop();
  });
};

fetchMapping();

addScripts../plugins/source/source.js:
const fromBinary = (encoded) => {
  const binary = atob(encoded);
  const bytes = new Uint8Array(binary.length);
  for (let i = 0; i < bytes.length; i += 1) {
    bytes[i] = binary.charCodeAt(i);
  }
  return String.fromCharCode(...new Uint16Array(bytes.buffer));
};

const supportsPopover = () => HTMLElement.prototype.hasOwnProperty("popover");

window.slidesk.changeSource = () => {
  if (supportsPopover())
    document.querySelector("#sd-source pre").innerText = fromBinary(
      window.slidesk.slides[window.slidesk.currentSlide].getAttribute(
        "data-source",
      ),
    );
};

if (!supportsPopover()) {
  document.querySelector("#sd-showSource").style.display = "none";
  document.querySelector("#sd-source").style.display = "none";
}

