window.slidesk.next = () => {
  if (checkStepConditions()) {
    if (window.slidesk.step === window.slidesk.maxSteps) {
      window.slidesk.oldNext();
    } else {
      window.slidesk.$lis[window.slidesk.step++].classList.remove(
        "step-hidden",
      );
    }
  } else window.slidesk.oldNext();
};

addStyles../plugins/steps/steps.css:
.steps li {
  opacity: 1;
  transition: opacity var(--animationTimer);
}

.steps li.step-hidden {
  opacity: 0;
}
